import React from "react";
import "../styles/Education.css";

const Education = () => {
  const education = [
    {
      school: "University of California, Berkeley",
      degree: "B.S. in Computer Science and Statistics",
      duration: "2023 - 2027",
      location: "Berkeley, CA",
      gpa: "3.8/4.0",
      description: "Focusing on backend development, ML/AI, and probabilistic risk assessment."
    }
  ];

  const courses = [
    {
      category: "Computer Science",
      items: [
        { name: "Data Structures and Algorithms", code: "CS 61B", grade: "A+", link: "https://cs61b.org/" },
        { name: "Machine Learning", code: "CS 189", grade: "A", link: "https://cs189.org/" },
        { name: "Software Engineering", code: "CS 169", grade: "A", link: "https://cs169.org/" }
      ]
    },
    {
      category: "Statistics & Data Science",
      items: [
        { name: "Probability Theory", code: "STAT 134", grade: "A", link: "https://www.stat.berkeley.edu/~aldous/134/" },
        { name: "Statistical Inference", code: "STAT 135", grade: "A-", link: "https://www.stat.berkeley.edu/~wfithian/courses/stat135/" },
        { name: "Data Science", code: "STAT 140", grade: "A", link: "https://www.stat.berkeley.edu/~wfithian/courses/stat140/" }
      ]
    },
    {
      category: "Mathematics",
      items: [
        { name: "Linear Algebra", code: "MATH 54", grade: "A", link: "https://math.berkeley.edu/~nadler/54.html" },
        { name: "Multivariable Calculus", code: "MATH 53", grade: "A", link: "https://math.berkeley.edu/~nadler/53.html" },
        { name: "Discrete Mathematics", code: "CS 70", grade: "A", link: "https://cs70.org/" }
      ]
    }
  ];

  return (
    <div className="education-container">
      <h1>Education</h1>
      
      {/* Educational Experience Section */}
      <section className="education-experience">
        <h2>Educational Experience</h2>
        <div className="education-timeline">
          {education.map((edu, index) => (
            <div key={index} className="education-card">
              <div className="education-header">
                <h3>{edu.school}</h3>
                <span className="duration">{edu.duration}</span>
              </div>
              <div className="education-details">
                <div className="degree">{edu.degree}</div>
                <div className="location">{edu.location}</div>
                <div className="gpa">GPA: {edu.gpa}</div>
                <p className="description">{edu.description}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Courses Section */}
      <section className="courses-section">
        <h2>Relevant Courses</h2>
        <div className="courses-grid">
          {courses.map((category, index) => (
            <div key={index} className="course-category">
              <h3>{category.category}</h3>
              <div className="course-list">
                {category.items.map((course, courseIndex) => (
                  <a 
                    key={courseIndex} 
                    href={course.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="course-item"
                  >
                    <div className="course-info">
                      <span className="course-code">{course.code}</span>
                      <span className="course-name">{course.name}</span>
                    </div>
                    <span className="course-grade">{course.grade}</span>
                  </a>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Education;
